  {% assign posts_limit = site.feed.posts_limit | default: 10 %}
  {% assign posts = site.posts | where_exp: "post", "post.draft != true" | sort: "date" | reverse %}
  {% for post in posts limit: posts_limit %}
    {% assign post_title = post.title | smartify | strip_html | normalize_whitespace | xml_escape %}
    {% assign absolute_src_prefix = 'src="' | append: site.url | append: '/' %}
    {% assign absolute_src_prefix_sq = "src='" | append: site.url | append: '/' %}
    {% assign absolute_href_prefix = 'href="' | append: site.url | append: '/' %}
    {% assign absolute_href_prefix_sq = "href='" | append: site.url | append: '/' %}
    {% assign post_content = post.content
      | replace: 'src="/', absolute_src_prefix
      | replace: "src='/", absolute_src_prefix_sq
      | replace: 'src="assets/', absolute_src_prefix
      | replace: "src='assets/", absolute_src_prefix_sq
      | replace: 'href="/', absolute_href_prefix
      | replace: "href='/", absolute_href_prefix_sq
      | replace: 'href="assets/', absolute_href_prefix
      | replace: "href='assets/", absolute_href_prefix_sq
    %}

